CREATE TABLE IF NOT EXISTS Produtos(`pro_id` INT NOT NULL AUTO_INCREMENT, `pro_nome_produto` VARCHAR(255), `pro_codigo_interno` VARCHAR(255), `pro_codigo_barras` VARCHAR(255), `pro_habilitar_nota_fiscal` VARCHAR(255), `pro_peso` VARCHAR(255), `pro_largura` VARCHAR(255), `pro_altura` VARCHAR(255), `pro_comprimento` VARCHAR(255), `pro_descricao` VARCHAR(255), `pro_fornecedor_id` VARCHAR(255), `pro_minimo` INT(255), `pro_maximo` INT(255), `pro_quant_atual` INT(255), PRIMARY KEY (`pro_id`)) ENGINE = InnoDB;
CREATE TABLE IF NOT EXISTS Setores(`set_id` INT NOT NULL AUTO_INCREMENT, `set_codigo` VARCHAR(255), `set_nome_setor` VARCHAR(255), `set_funcionario_id` VARCHAR(255), `set_observacoes` VARCHAR(255), PRIMARY KEY (`set_id`)) ENGINE = InnoDB;
