CREATE TABLE IF NOT EXISTS Contato(`con_id` INT NOT NULL AUTO_INCREMENT, `con_tel_cel_cont` VARCHAR(25), `con_tel_comercial_con` VARCHAR(25), `con_email_contato` VARCHAR(255), `con_cel2` VARCHAR(255), `con_cliente_id` INT(11), PRIMARY KEY (`con_id`)) ENGINE = InnoDB;
CREATE TABLE IF NOT EXISTS Detalhes(`det_id` INT NOT NULL AUTO_INCREMENT, `det_peso` VARCHAR(25), `det_altura` VARCHAR(25), `det_largura` VARCHAR(25), `det_data_validade` VARCHAR(25), `det_descricao` VARCHAR(255), PRIMARY KEY (`det_id`)) ENGINE = InnoDB;
CREATE TABLE IF NOT EXISTS Endereco(`end_id` INT NOT NULL AUTO_INCREMENT, `end_nome_logradouro` VARCHAR(255), `end_cep` VARCHAR(255), `end_numero` VARCHAR(255), `end_complemento` VARCHAR(255), `end_cidade` VARCHAR(255), `end_uf` VARCHAR(255), PRIMARY KEY (`end_id`)) ENGINE = InnoDB;
CREATE TABLE IF NOT EXISTS Cliente(`cli_id` INT NOT NULL AUTO_INCREMENT, `cli_cpf_cnpj` VARCHAR(255), `cli_rg` VARCHAR(255), `cli_data_nascimento` VARCHAR(255), `cli_nome_fantasia` VARCHAR(255), `cli_razacao_social` VARCHAR(255), `cli_responsavel` VARCHAR(255), `cli_situacao` VARCHAR(255), `cli_inscricao_estadual` VARCHAR(255), `cli_inscricao_municiapl` VARCHAR(255), `cli_inscricao_suframa` VARCHAR(255), `cli_observacoes` VARCHAR(255), `cli_tipo_contribuinte` VARCHAR(255), `cli_tipo_de_pessoa` VARCHAR(255), PRIMARY KEY (`cli_id`)) ENGINE = InnoDB;
CREATE TABLE IF NOT EXISTS Estoque(`est_id` INT NOT NULL AUTO_INCREMENT, `est_estoque_minimo` INT(11), `est_estoque_maximo` INT(11), `est_quant_atual` INT(11), PRIMARY KEY (`est_id`)) ENGINE = InnoDB;
CREATE TABLE IF NOT EXISTS Etiqueta(`eti_id` INT NOT NULL AUTO_INCREMENT, `eti_data_etiqueta` VARCHAR(25), `eti_horario_etiqueta` VARCHAR(25), PRIMARY KEY (`eti_id`)) ENGINE = InnoDB;
CREATE TABLE IF NOT EXISTS Setor(`set_id` INT NOT NULL AUTO_INCREMENT, `set_nome` VARCHAR(255), PRIMARY KEY (`set_id`)) ENGINE = InnoDB;
CREATE TABLE IF NOT EXISTS Produtos(`pro_id` INT NOT NULL AUTO_INCREMENT, `pro_descricao` VARCHAR(255), `pro_codigo_interno` VARCHAR(255), `pro_codigo_barras` VARCHAR(255), `pro_habilitar_nota_fiscal` VARCHAR(255), PRIMARY KEY (`pro_id`)) ENGINE = InnoDB;
CREATE TABLE IF NOT EXISTS Status(`sta_id` INT NOT NULL AUTO_INCREMENT, `sta_estado` VARCHAR(25), PRIMARY KEY (`sta_id`)) ENGINE = InnoDB;
CREATE TABLE IF NOT EXISTS ValorProdutos(`val_id` INT NOT NULL AUTO_INCREMENT, `val_valor_custo` VARCHAR(25), `val_despesas_acessorias` VARCHAR(25), `val_outras_despesas` VARCHAR(25), PRIMARY KEY (`val_id`)) ENGINE = InnoDB;
CREATE TABLE IF NOT EXISTS Tags(`tag_id` INT NOT NULL AUTO_INCREMENT, `tag_codigo_tag` VARCHAR(255), `tag_descricao` VARCHAR(255), `tag_data_de_cadastro` VARCHAR(25), PRIMARY KEY (`tag_id`)) ENGINE = InnoDB;
CREATE TABLE IF NOT EXISTS Usuario(`usu_id` INT NOT NULL AUTO_INCREMENT, `usu_nome_de_usuario` VARCHAR(30), `usu_senha` VARCHAR(255), `usu_categoria` VARCHAR(30), PRIMARY KEY (`usu_id`)) ENGINE = InnoDB;
